import React, { useState } from 'react';

const TripPlanner = () => {
  const [destination, setDestination] = useState('');
  const [dates, setDates] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handlePlanTrip = (e) => {
    e.preventDefault();

    // Simple validation
    if (!destination || !dates) {
      setErrorMessage('Please enter both destination and travel dates');
      return;
    }

    // Reset error message
    setErrorMessage('');

    // Trip planning logic (replace with real logic)
    console.log('Trip Destination:', destination);
    console.log('Travel Dates:', dates);
    alert('Trip Planned Successfully!');
  };

  return (
    <div>
      <h2>Plan Your Trip</h2>
      <form onSubmit={handlePlanTrip}>
        <div>
          <label>Destination:</label>
          <input
            type="text"
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
            placeholder="Enter your destination"
          />
        </div>
        <div>
          <label>Travel Dates:</label>
          <input
            type="text"
            value={dates}
            onChange={(e) => setDates(e.target.value)}
            placeholder="Enter your travel dates"
          />
        </div>
        {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
        <button type="submit">Plan Trip</button>
      </form>
    </div>
  );
};

export default TripPlanner;
